var initiallState=[];
const tasks=(state=initiallState,action)=>{
    switch(action.type){
        case 'ADD-PRODUCT':
            console.log('okokkokokokoko')
           console.log(action.product)
            state=[action.tasks]
            return [...state]
        case 'GET-PRODUCT':
            console.log('ahiihii')
            state=action.tasks
            return [...state]
            default: return [...state];
    }
}
export default tasks;
