import callApi from './../commons/axiosService';
import product from '../reducers/products';
export const getData=()=>{
    return (dispatch)=>{
       callApi('GET',null,'').then(res=>{
        dispatch(actFetchProducrs(res.data))
       });
    }
}
export const getItemProduct=(id)=>{
    return (dispatch)=>{
        console.log("res.data",id)
        callApi('GET',null,id).then(res=>{
            dispatch(fetchItemProduct(res.data))
        })
    }
}
export const  actFetchProducrs=(products)=>{
    return{
        type:'FETCH-PRODUCTS',
        products:products
    }
}
export const fetchItemProduct=(product)=>{
    return{
        type:'FETCH-ITEM-PRODUCT',
        product:product,
    }
}
export const addProductInCart=(t)=>{
    return {
        type:'ADDPRODUCT',
        product
    }
}
