var initiallState=[];
const tasks=(state=initiallState,action)=>{
    switch(action.type){
        case 'ADD-PRODUCT':
           action.tasks.push(action.product)
            state=[...action.tasks]
            return [...state]
        case 'GET-PRODUCT':
            state=action.tasks
            return [...state]
        case 'REMOVE-PRODUCT':
            console.log(action.id)
            console.log(action.tasks)

            return [...state] 
            default: return [...state];
        }
}
export default tasks;
