import React, { Component } from 'react';
import {connect} from 'react-redux';
import {Money} from './../../actions/index';
class TotalMoney extends Component {
    componentDidMount=()=>{
        this.props.Sum(this.props.tasks)   
    }
    render() {
        var {monyall}=this.state
        return (
        <div className="total-money">{monyall}fdh</div>
            )
    }
}
const mapStateToProps=(state)=>{
    return{
        tasks:state.tasks,
        monyall:state.monyall
    }
}
const mapDispatchToProps=dispatch=>{
    return {
        Sum:(product)=>{
           dispatch(Money(product))
         }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(TotalMoney);
