import React, { Component } from 'react';
import { connect } from 'react-redux';
import { deleteProduct } from './../../actions/index';
class ItemCart extends Component {
    removeItem = (id) => {
        var { tasks } = this.props
        console.log(tasks)
        if (tasks) {
            this.props.removeItem(id, tasks)
        }
    }
    onHandleChange=(event)=>{
       // var value=event.target.value;
        console.log("value")
    }
    render() {
        var { stt } = this.props
        var { name } = this.props
        var { img } = this.props
        var { price } = this.props
        var { id } = this.props
        return (
            <tr>
                <th>{stt}</th>
                <th>
                    <div>{name}</div>
                    <img src={img} />
                </th>
                <th>
                    <input name=""
                           className="input"
                           type="text"
                           onChange={this.onHandleChange()}
                           />
                </th>
                <th>{price}(VNĐ)</th>
                <th>TỔNG</th>
                <th>
                    <div onClick={() => this.removeItem(id)}>X</div></th>
            </tr>
        )
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        removeItem: (id, tasks) => {
            dispatch(deleteProduct(id, tasks))
        }
    }
}
const mapStateToProps = (state) => {
    return {
        tasks: state.tasks
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(ItemCart);
