import callApi from './../commons/axiosService';
import product from '../reducers/products';
import TotalMoney from '../components/CartItem/TotalMoney';
// GET  LISTOCLOCK
export const getData = () => {
    return (dispatch) => {
        callApi('GET', null, 'listoclock').then(res => {
            dispatch(actFetchProducrs(res.data))
        });
    }
}
export const getItemProduct = (id) => {
    return (dispatch) => {
        callApi('GET', null, `listoclock/${id}`).then(res => {
            dispatch(fetchItemProduct(res.data))
        })
    }
}
export const actFetchProducrs = (products) => {
    return {
        type: 'FETCH-PRODUCTS',
        products: products
    }
}
export const fetchItemProduct = (product) => {
    return {
        type: 'FETCH-ITEM-PRODUCT',
        product: product,
    }
}
export const sortMaxMin=(products)=>{
    return{
        type:'SORT-MAX-MIN',
        products:products
    }
}
export const sortMinMax=(products)=>{
    return{
        type:'SORT-Min-Max',
        products:products
    }
}
// GET TASKSOCLOCK
export const addProductInCart = (product,tasks) => {
    return {
        type: 'ADD-PRODUCT',
        product: product,
    }
}
export const getProductInCart = () => {
    return {
        type: 'GET-PRODUCT',
    }
}
export const removeProduct=(id,tasks)=>{
    return{
        type:'REMOVE-PRODUCT',
        id:id,
        tasks:tasks
    }
}
export const count=(value,id)=>{
    return{
        type:'COUNT',
        value:value,
        id:id
    }
}
export const Money=(value,id)=>{
    return{
        type:'TOTAL-MONEY',
        value:value,
        id:id
    }
}
export const sumMoney=(tasks)=>{
    return{
        type:'SUM-MONEY',
        tasks:tasks,     
    }
}
export const removeAllremoveAll=()=>{
    return{
        type:'REMOVE-ALL',
    }
}
