import React, { Component } from 'react';
import {connect} from 'react-redux';
import {getTasks,addTask} from './../../actions/index';
import ItemCart from './ItemCart2';
class TableCart extends Component {
    fineIndex=(products,id)=>{
        var index=-1;
        for(var i=0;i<products.length;i++){
           // products[i].id=id
            if(id===products[i].id){
                products[i].id=id
              index=i;
            }
        }
        return index
    }
    componentDidMount=()=>{
        var {tasks}=this.props;
        var {product}=this.props;
        var index=this.fineIndex(tasks,product.id)
        if(product.id &&index==-1){
        this.props.addItemTasks(product,tasks)
        }
        if(tasks)
        this.props.getItemTasks()
    }
    componentWillReceiveProps(nextProps){
        // var {tasks}=this.props;
        // var {product}=this.props;
        // nextProps.getItemTasks(product,tasks)
    }
    render() {
        var {tasks}=this.props;
        if(tasks){
        var task=tasks.map((task,index)=>{
            console.log(task.img)
            return <ItemCart
                     key={index}
                     id={task.id}
                     stt={index}
                     name={task.name}
                     img={task.img}
                     price={task.price}
                   />
        })
    }
        return (
            <div className="wrap-cart">
            <div className="title">
                <h1>Mua Thanh Toán Nhanh</h1>
            </div>
            {/* //=======Table=====/// */}
            <div className="wp-table">
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <th>STT</th>
                            <th>TÊN</th>
                            <th>SỐ LƯỢNG</th>
                            <th>ĐƠN GIÁ(VNĐ)</th>
                            <th>TỔNG</th>
                            <th>XÓA</th>
                        </tr>
                    </thead>
                    <tbody>
                        {task}
                    </tbody>
                </table>
                <div className="total-money">Tổng (VNĐ)</div>
            </div>
        </div>

            )
    }
}
const mapStateToProps=(state)=>{
    return{
        tasks:state.tasks,
        product:state.product
    }
}
const mapDispatchToProps=dispatch=>{
    return {
        getItemTasks:(product)=>{
            dispatch(getTasks(product))
        },
        addItemTasks:(product,tasks)=>{
            dispatch(addTask(product,tasks))
        }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(TableCart);
