import React, { Component } from 'react';
import {connect} from 'react-redux';
import {sumMoney,getProductInCart} from './../../actions/index';
class TotalMoney extends Component {
    componentDidMount=()=>{ 
        console.log(this.props.tasks)
        this.props.sumMoneyAll(this.props.tasks)
        this.props.getTasks();
    }
    render() {
        var {tasks}=this.props
        console.log(tasks)
        var {monyall}=this.props
        return (
        <div className="total-money">{monyall}(VNƒê}</div>
            )
         }
}
const mapStateToProps=(state)=>{
    return{
        tasks:state.tasks,
        monyall:state.monyall
    }
}
const mapDispatchToProps=dispatch=>{
    return {
         sumMoneyAll:(tasks)=>{
            dispatch(sumMoney(tasks))
          },
          getTasks:()=>{
            dispatch(getProductInCart())
          }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(TotalMoney);
