import callApi from './../commons/axiosService';
import product from '../reducers/products';
// GET  LISTOCLOCK
export const getData = () => {
    return (dispatch) => {
        callApi('GET', null, 'listoclock/').then(res => {
            dispatch(actFetchProducrs(res.data))
        });
    }
}
export const getItemProduct = (id) => {
    return (dispatch) => {
        console.log("res.data", id)
        callApi('GET', null, `listoclock/${id}`).then(res => {
            dispatch(fetchItemProduct(res.data))
        })
    }
}
export const actFetchProducrs = (products) => {
    return {
        type: 'FETCH-PRODUCTS',
        products: products
    }
}
export const fetchItemProduct = (product) => {
    return {
        type: 'FETCH-ITEM-PRODUCT',
        product: product,
    }
}
// GET TASKSOCLOCK
export const getTasks = (product,tasks) => {
    if (product.id) {
        return (dispatch) => {
            callApi('POST', product, 'tasksoclock').then(res => {
                dispatch(addProductInCart(product,tasks))
            })
        }
    }
    else {
        return (dispatch) => {
            callApi('GET', null,'tasksoclock').then(res => {
                dispatch(getProductInCart(res.data))
            })
        }
    }
}
export const addProductInCart = (product,tasks) => {
    return {
        type: 'ADD-PRODUCT',
        product: product,
        tasks:tasks,
    }
}
export const getProductInCart = (tasks) => {
    return {
        type: 'GET-PRODUCT',
        tasks:tasks
    }
}
export const deleteProduct=(id)=>{
    return dispatch=>{
        callApi('DELETE',null,`tasksoclock/${id}`).then(res=>{
           dispatch(removeProduct(res.data.id))
        })
    }
}
export const removeProduct=(id)=>{
    return{
        type:'REMOVE-PRODUCT',
        id:id
    }
}
