var initiallState=[];
const fineIndex=(products,id)=>{
    var index=-1;
    for(var i=0;i<products.length;i++){
       // products[i].id=id
        if(id===products[i].id){
          index=i;
        }
    }
    return index
}
const tasks=(state=initiallState,action)=>{
    switch(action.type){
        case 'ADD-PRODUCT':
            console.log(action.tasks)
            console.log(action.product.id)
            var index=fineIndex(action.tasks,action.product.id)    
        if(index===-1){
           action.tasks.push(action.product)
            state=[...action.tasks]
        }
            return [...state]
        case 'GET-PRODUCT':
            state=action.tasks
            console.log(action.tasks)
            return [...state]
        case 'REMOVE-PRODUCT':
            var index=fineIndex(action.tasks,action.id)
            console.log(action.tasks)  
            console.log(action.id)  
           action.tasks.splice(index,1)
           state=action.tasks;
            console.log(state)
            return [...state] 
            default: return [...state];
        }
}
export default tasks;
