import React, { Component } from 'react';
import { connect } from 'react-redux';
import { sortMaxMin } from './../../actions/index';
class SortList extends Component {
    sortMaxMax = () => {
        this.props.sortMax();
        var {products}=this.props
        console.log(products)
    }
    render() {
        return (
            <div className="sort-list">
                <div class="dropdown">
                    <button type="button" class="btn btn-primary dropdown-toggle" data-toggle="dropdown">
                    Sắp xếp theo
                    </button>
                    <div class="dropdown-menu">
                        <a class="dropdown-item">Sắp xếp theo</a>
                        <a class="dropdown-item"  onClick={this.sortMaxMax}>Giá từ cao đến thấp</a>
                        <a class="dropdown-item " >Bán chạy nhất</a>
                        <a class="dropdown-item " >Khuyến mãi</a>
                    </div>
                </div>
            </div>
        )
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        sortMax: () => {
            dispatch(sortMaxMin(this.props.products))
        }
    }
}
const mapStateTopProps= (state)=>{
    return{
        products:state.products
    }
}
export default connect(mapStateTopProps, mapDispatchToProps)(SortList);
