import React, { Component } from 'react';
import p1 from './../../img/p1.PNG';
import p6 from './../../img/p6.PNG';
import p7 from './../../img/p7.PNG';
import { fetchItemProduct} from './../../actions/index';
import callApi from './../../commons/axiosService';
import {connect} from 'react-redux';
class LeftShow extends Component {
     componentDidMount(){
    var {id}=this.props
    callApi('GET',null,id).then(res=>{
        console.log(res)
        this.props.getItemProduct(res.data)
    })    
}
    // componentWillReceiveProps(nextProps){
    //     this.props.getProduct(nextProps.product)
    // }
    render() {
        var {products}=this.props;
        var {id}=this.props
        console.log(products)
        console.log(id)
        return (
            <div className="col-sm-4">
                <div className="wp-img">
                    <img src={"product.img"} />
                </div>
                <div className="status">
                    <button className="like"><span>
                        <img src={p6} alt />
                    </span><span className="span-l">Thích 0</span></button>
                    <button className="share">
                        <span>
                            <img src={p7} alt />
                        </span><span className="span-s">Chia sẻ 0</span></button>
                </div>
            </div>

        )
    }
}
const mapStateToProps=(state)=>{
    return{
        products:state.products
    }
}
const mapDispatchToProps=(dispatch)=>{
    return{
        getItemProduct:(product)=>{
            dispatch(fetchItemProduct(product))
        }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(LeftShow);
