import React, { Component } from 'react';
import ItemProduct from './ItemProduct';
import {connect} from 'react-redux';
import {getData} from './../../../actions/index';
import {Link} from "react-router-dom"
class MainRight extends Component {
    componentDidMount(){
        this.props.fetchAllProducts();
    }
    render() {
     var {products}=this.props
    var tasks=products.map((product,index)=>{
        if(index<12){
         return <ItemProduct
                   key={index}
                   product={product}
                   img={product.img}
                   id={product.id}
                   name={product.name}
                   introduce={product.introduce}
                   price={product.price}
         />
       }
     })
        return (
       <div className="right-item-cat" style={{width: '73%'}}>
  <div className="row">
   {tasks}
  </div>
   <div className="list-pages">
    <Link to="/donghonam">1</Link>
    <Link to="/donghonam/page1">2</Link>
    <Link to="/donghonam/page11">3</Link>
    <Link to="/donghonam/page11">4</Link>
    <Link to="/donghonam/page11">5</Link>
  </div>
</div>
        )
    }
}
const mapStateToProps=(state)=>{
    return{
        products:state.products
    }
}
const mapDispatchToProps=(dispatch,props)=>{
    return{
        fetchAllProducts:()=>{
            dispatch(getData())
        }
    }
}
export default  connect(mapStateToProps,mapDispatchToProps)(MainRight);
