import React, { Component } from 'react';
import {connect} from 'react-redux';
import {addProductInCart,Money} from './../../actions/index';
class totalMoney extends Component {
    componentDidMount=()=>{
        this.props.Sum(this.props.tasks)   
    }
    render() {
        return (
        <div className="total-money">{}fdh</div>
            )
    }
}
const mapStateToProps=(state)=>{
    return{
        tasks:state.tasks,
    }
}
const mapDispatchToProps=dispatch=>{
    return {
        Sum:(product)=>{
           dispatch(Money(product))
         }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(totalMoney);
