import { sumMoney } from "../actions";

const initialStateState=1;
 const sum=(tasks)=>{
     console.log(tasks)
       var task=tasks.reduce((a,b)=>{
        console.log(a.sumPrice)
           return a.sumPrice+b.sumPrice
       })
       console.log(task)
       return task
 }
const summoney=(state=initialStateState,action)=>{
    switch(action.type){
            case 'SUM-MONEY':
                console.log(sum(action.tasks))
              //  console.log(Sum)
               return state
              default: return state
    }
}
export default summoney;
