import React, { Component } from 'react';
import { connect } from 'react-redux';
import { removeProduct,count } from './../../actions/index';
import $ from 'jquery';
class ItemCart extends Component {
    // removeItem = (id) => {
    //     var { tasks } = this.props
    //     console.log(tasks)
    //     if (tasks) {
    //         this.props.removeItem(id, tasks)
    //     }
    // }
    componentDidMount = (event) => {
        $('.input').keypress( (event)=> {
            if (event.keyCode == 13 || event.which == 13) {
                console.log(event.target.value)
                this.props.counts(event.target.value)    
            }
            
        });
        
    }
    render() {
        var {count}=this.props
        var { stt } = this.props
        var { name } = this.props
        var { img } = this.props
        var { price } = this.props
        var { id } = this.props
        return (
            <tr>
                <th>{stt}</th>
                <th>
                    <div>{name}</div>
                    <img src={img} />
                </th>
                <th>
                    <input name=""
                        className="input"
                        type="text"
                        value={count}
                    />
                </th>
                <th>{price}(VNƒê)</th>
                <th>{count*price}</th>
                <th>
                    <div onClick={() => this.removeItem(id)}>X</div></th>
            </tr>
        )
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        removeItem: (id, tasks) => {
            dispatch(removeProduct(id, tasks))
        },
        counts: (value) => {
            dispatch(count(value))
        }
    }
}
const mapStateToProps = (state) => {
    return {
        tasks: state.tasks,
        count:state.count
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(ItemCart);
