import React, { Component } from 'react';
import {connect} from 'react-redux';
import {getTasks} from './../../actions/index';
import ItemCart from './ItemCart2';
class TableCart extends Component {
    componentDidMount=()=>{
        var {product}=this.props;
        this.props.getItemTasks(product)
    }
    render() {
        var {tasks}=this.props;
        var task=tasks.map((task,index)=>{
            console.log(task.img)
            return <ItemCart
                     key={index}
                     stt={index}
                     name={task.name}
                     img={task.img}
                     price={task.price}
                   />
        })
        return (
            <div className="wrap-cart">
            <div className="title">
                <h1>Mua Thanh Toán Nhanh</h1>
            </div>
            {/* //=======Table=====/// */}
            <div className="wp-table">
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <th>STT</th>
                            <th>TÊN</th>
                            <th>SỐ LƯỢNG</th>
                            <th>ĐƠN GIÁ(VNĐ)</th>
                            <th>TỔNG</th>
                            <th>XÓA</th>
                        </tr>
                    </thead>
                    <tbody>
                        {task}
                    </tbody>
                </table>
                <div className="total-money">Tổng (VNĐ)</div>
            </div>
        </div>

            )
    }
}
const mapStateToProps=(state)=>{
    return{
        tasks:state.tasks,
        product:state.product
    }
}
const mapDispatchToProps=dispatch=>{
    return {
        getItemTasks:(product)=>{
            dispatch(getTasks(product))
        }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(TableCart);
