import React, { Component } from 'react';
import {connect} from 'react-redux';
import {sortMaxMin} from './../../actions/index';
class SortList extends Component {
    sortMax=()=>{
        // this.props.sortMax();
        console.log('okokokokokokgl')
    }
    render() {
        return (
            <div className="sort-list">
                    <select className="order-select">
                        <option value>Sắp xếp theo</option>
                        <option value >Giá từ thấp đến cao</option>
                        <option value onClick={this.sortMax}>Giá từ cao đến thấp</option>
                        <option value >Bán chạy nhất</option>
                        <option value >Khuyến mãi</option>
                    </select>
                </div>


        )
    }
}
const mapDispatchToProps=(dispatch)=>{
    return{
        sortMax:()=>{
            dispatch(sortMaxMin())
        }
    }
}
export default connect(null,mapDispatchToProps)(SortList);
