import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import style from './../pages/style';
import {connect} from 'react-redux';
class Header extends Component {
  componentDidMount(){ 
 style();
  }
    render() {
      style();
        return (
            <header>
            <div className="top-header mac-flex mac-flex-justify mac-flex-middle">
              <div className="left-header">
                <img className="logo-img" src="https://xwatch.vn/images/config/xwatch-logo-small_1562275266.png" />
              </div>
              <div className="right-header mac-flex mac-flex-middle">
                <div className="search">
                  <i className="fab fa-sistrix icon-search" style={{fontSize: 24}} />
                  <input type="text" name className="input-search" placeholder="search" />
                </div>
                <div className="store">
                  Cửa Hàng
                </div>
                <div className="call">
                  <i className="fas fa-phone phone" />
                  <div className="buy">MUA HÀNG</div>
                  <h2>1900.0325</h2>
                </div>
                <div className="cart">
                  <Link to='/cartitem'>
                  <img src="https://img.icons8.com/ultraviolet/40/000000/add-shopping-cart.png" />
                  <span className='cart-count'>{this.props.tasks.length}</span>
                  </Link>
                </div>
              </div>
            </div>
            <nav>
              <div className="navbar">
                <ul className="wrap-nav mac-flex">
                  <li className="icon-home">
                  <Link to="/"><img src="https://png.pngtree.com/png-clipart/20190520/original/pngtree-home-icon-png-image_3568907.jpg" style={{width: 25, height: 25}} /></Link>
                  </li>
                  <li><Link to="/donghonam">VỀ XWATCH</Link></li>
                  <li><a href="#">THƯƠNG HIỆU</a></li>
                  <li><Link to="/donghonam">ĐỒNG HỒ NAM</Link></li>
                  <li><a href="#">ĐỒNG HỒ NỮ</a></li>
                  <li><a href="#">THẨM ĐỊNH ĐỒNG HỒ</a></li>
                  <li><a href="#">KIẾN THỨC</a></li>
                  <li><a href="#">SỬA CHỬA</a></li>
                </ul>
              </div>
            </nav>
          </header>
          

        )
    }
}
const mapStateToProps=(state)=>{
  return{
    tasks:state.tasks
  }
}
export default connect(mapStateToProps,null)(Header);
