import React, { Component } from 'react';
import { connect } from 'react-redux';
import { removeProduct,count } from './../../actions/index';
import $ from 'jquery';
class ItemCart extends Component {
    constructor(props){
        super(props);
        this.state={
           count:this.props.amount
        }
        var sumPrice=1;
    }
    removeItem = (id) => {
            this.props.removeItem(id)
    }
    onChange = (event) => {
        this.setState({
            count:event.target.value
        })
        $('.input').keypress( (event)=> {
            if (event.keyCode == 13 || event.which == 13) {
                this.props.counts(this.props.amount,this.props.id)
                this.sumPrice=this.state.count*this.props.price    
            }   
        });    
    }
    render() {
        var { amount } = this.props
        console.log(amount)
        var { stt } = this.props
        var { name } = this.props
        var { img } = this.props
        var { price } = this.props
        var { id } = this.props
        return (
            <tr>
                <th>{stt}</th>
                <th>
                    <div>{name}</div>
                    <img src={img} />
                </th>
                <th>
                    <input name=""
                        className="input"
                        type="text"
                        value={this.state.count}
                        onChange={this.onChange}
                    />
                </th>
                <th>{price}(VNĐ)</th>
                <th>{this.sumPrice}(VNĐ)</th>
                <th>
                    <div onClick={() => this.removeItem(id)}>X</div></th>
            </tr>
        )
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        removeItem: (id) => {
            dispatch(removeProduct(id))
        },
        counts: (value,id) => {
            dispatch(count(value,id))
        }
    }
}
const mapStateToProps = (state) => {
    return {
        tasks: state.tasks,
        count:state.count
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(ItemCart);
