var initiallState=[];
const fineIndex=(products,id)=>{
    var index=-1;
    for(var i=0;i<products.length;i++){
        if(id===products[i].id){
          index=i;
        }
    }
    return index
}
const tasks=(state=initiallState,action)=>{
    switch(action.type){
        case 'ADD-PRODUCT':
           action.tasks.push(action.product)
            state=[...action.tasks]
            return [...state]
        case 'GET-PRODUCT':
            if(action.tasks){
            state=action.tasks
        }
            return [...state]
        case 'REMOVE-PRODUCT':
            var index=fineIndex(action.tasks,action.id)
            console.log(action.tasks)  
            console.log(action.id)  
            state=action.tasks.splice(index,1)
            console.log(state)
            return [...state] 
