const initialStateState=1;
 const sum=()=>{
    var todos=JSON.parse(localStorage.getItem('tasks'))
       var task=todos.reduce((a,b)=>{
           return a + b.sumPrice
       },0)
       return task
 }
const summoney=(state=initialStateState,action)=>{
    switch(action.type){
            case 'SUM-MONEY':
              state=sum()
               return state
              default: return state
    }
}
export default summoney;
