import callApi from './../commons/axiosService';
import product from '../reducers/products';
import totalMoney from '../components/CartItem/totalMoney';
// GET  LISTOCLOCK
export const getData = () => {
    return (dispatch) => {
        callApi('GET', null, 'listoclock').then(res => {
            dispatch(actFetchProducrs(res.data))
        });
    }
}
export const getItemProduct = (id) => {
    return (dispatch) => {
        console.log("res.data", id)
        callApi('GET', null, `listoclock/${id}`).then(res => {
            dispatch(fetchItemProduct(res.data))
        })
    }
}
export const actFetchProducrs = (products) => {
    return {
        type: 'FETCH-PRODUCTS',
        products: products
    }
}
export const fetchItemProduct = (product) => {
    return {
        type: 'FETCH-ITEM-PRODUCT',
        product: product,
    }
}
// GET TASKSOCLOCK
export const addProductInCart = (product,tasks) => {
    return {
        type: 'ADD-PRODUCT',
        product: product,
    }
}
export const getProductInCart = (tasks) => {
    return {
        type: 'GET-PRODUCT',
        tasks:tasks
    }
}
export const removeProduct=(id,tasks)=>{
    return{
        type:'REMOVE-PRODUCT',
        id:id,
        tasks:tasks
    }
}
export const count=(value)=>{
    return{
        type:'COUNT',
        value:value
    }
}
export const SMoney=(value)=>{
    return{
        type:'TOTAL-MONEY',
        value:value
    }
}
